parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "array",
  "items": {
    "type": "object",
    "properties": {
      "scene": {
        "type": "integer",
        "minimum": 1,
        "description": "The scene number, starting from 1."
      },
      "video_prompt": {
        "type": "string",
        "description": "The prompt for this scene, updated to include the ASMR idea."
      },
      "aspect_ratio": {
        "type": "string",
        "pattern": "^[0-9]+:[0-9]+$",
        "description": "The video aspect ratio for the scene (e.g., '16:9')."
      }
    },
    "required": ["scene", "video_prompt", "aspect_ratio"]
  }
}

parameters.jsCode:
const rawText = $json.output;
const lines = rawText.split('\n');

let data = {
  title: "",
  description: "",
  tags: "",
  hashtags: ""
};

for (const line of lines) {
  const cleanLine = line.trim();
  if (cleanLine.toLowerCase().startsWith("title:")) {
    data.title = cleanLine.replace(/^title:/i, "").trim();
  }
  if (cleanLine.toLowerCase().startsWith("description:")) {
    data.description = cleanLine.replace(/^description:/i, "").trim();
  }
  if (cleanLine.toLowerCase().startsWith("tags:")) {
    data.tags = cleanLine.replace(/^tags:/i, "").trim();
  }
  if (cleanLine.toLowerCase().startsWith("hashtags:")) {
    data.hashtags = cleanLine.replace(/^hashtags:/i, "").trim();
  }
}

return [{ json: data }];
